<template>
	<view>
		<view class="poem" :animation="poemShow">
			<view>十五年春华秋实</view>
			<view>风雨兼程</view>
			<view>十五年开拓进取</view>
			<view>砥砺前行</view>
		</view>
		<view class="btn" @click="toSelect"></view>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				poemShow: null
			}
		},
		onLoad() {
			uni.showShareMenu({
				withShareTicket: true
			})
			let animation = uni.createAnimation({
				duration: 1500,
				timingFunction: 'ease'
			})
			this.poemShow = animation
			animation.opacity(1).translateY(0).step()
			this.poemShow = animation.export()
		},
		onShareAppMessage() {
			return {
				title: 'GCU生日快乐-校庆头像制作',
				path: '/pages/index/index',
				desc: '和我一起制作头像框，为华广庆生！'
			}
		},
		methods: {
			toSelect: function() {
				uni.navigateTo({
					url: '../select/select'
				})
			}
		}
	}
</script>

